mapper.select:
EXEC dbo.SP_GetRecordByPage 
@tblName = 'Store_Products',
@fldName = 
	${storeProducts.order}
,
@PageSize = ${pageSize},
@PageIndex = ${pageIndex},
@IsReCount = 0,
@OrderType = 
	${storeProducts.desc}
,
@strWhere = '1=1 

				AND ProductName like ''%${storeProducts.productName,jdbcType=NVARCHAR}%'' 


				AND PTID = ${storeProducts.ptID} 


				AND PMCID = ${storeProducts.pmcID} 

				AND productState = 1 
		'

EXEC [SP_GetCount] 
@tblName = 'Store_Products',
@strWhere = '1=1 

				AND ProductName like ''%${productName,jdbcType=NVARCHAR}%'' 


				AND PTID = ${ptID} 


				AND PMCID = ${pmcID} 

				AND productState = 1 
		'
